import {IsNumber, IsString} from "class-validator";

import {
    BaseEntity,
    Column,
    Entity, JoinColumn, OneToOne,
    PrimaryGeneratedColumn,
} from 'typeorm';
import {Member} from "./member.entity";
import {ApiProperty} from "@nestjs/swagger";

@Entity({name: 'member_token'})
export class Token extends BaseEntity {
    @IsNumber()
    @PrimaryGeneratedColumn()
    @Column({primary: true, type: "int", unique: true, unsigned: true})
    @ApiProperty({
        example: '3'
    })
    idx: number = undefined

    @OneToOne(() => Member, member => member.tokenInfo, {
        onDelete: "CASCADE",
        onUpdate: "CASCADE",
        nullable: false
    })
    @JoinColumn()
    member: Member = undefined;

    @IsString()
    @Column({type: 'varchar', length: 1000, comment: '최근 사용 토큰'})
    @ApiProperty({
        example: 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..X2RlMAeluOA3fzq8hhxRwAfQF-nromnWh5snbhWzFp8'
    })
    token: string = undefined;

    @IsString()
    @Column({type: "varchar", unique: true, length: 50, comment: "유니크 코드"})
    @ApiProperty({
        example: 'opas4dyl4ly0ilq8dlgn95jrffwv0fi4ytto6xhy'
    })
    code: string = undefined;

    dataMigration(object: object) : void {
        for (let k in new Token()) {
            if (object[k] === undefined) continue;
            this[k] = object[k];
        }
    }

}